package learning.java.core.operators.bitshift;

public class Additive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int varAdditive = 0X01; //Hex Value pattern for 1
		System.out.println(varAdditive);
		System.out.println(varAdditive+5);
		System.out.println(varAdditive+1);
		System.out.println(varAdditive-5);
		System.out.println(varAdditive-1);
		System.out.println(varAdditive+3);
		System.out.println(varAdditive+2);
		System.out.println(varAdditive-100);
		System.out.println(varAdditive-1234567890);
		
	}

}
